.root-page{
  outline: 2px solid rgba(255,255,255,0.55);
  padding: 8px 0;
  z-index: 9999;
  cursor: pointer;
  right: 7px;
  bottom: 65px;
  border-radius: 100px;
  width: 40px;
  height: 40px;
  max-width: 50px;
  position: fixed;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  background-color: #0d7ae1;
  animation: breathing 1s ease 0s infinite alternate;
  overflow: hidden;
}

@keyframes breathing {
  0% {
    box-shadow: 0 0 5px 2px rgba(2, 70, 255, 0.2);
  }

  100% {
    box-shadow: 0 0 7px 3px rgba(2, 70, 255, 0.8);
  }
}

@keyframes breathing-deep {
  0% {
    box-shadow: 0 0 5px 2px rgba(2, 70, 255, 0.3);
  }

  100% {
    box-shadow: 0 0 10px 6px rgba(2, 70, 255, 0.8);
  }
}
.root-page>img{
  width: 25px;
  object-fit: cover;
}
.root-page>p{
  width: 30%;
  text-align: center;
  font-size: 11px;
  color: white;
}
.customer{
  width: 50px;
  height: 50px;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}
.customer>img{
  width: 42px;
  object-fit: cover;
  border-radius: 100px;
}
.card-container{
  overflow: hidden;
  z-index: 9999;
  cursor: pointer;
  height: 221px;
  width: 240px;
  transform: translateX(320px);
  opacity: 0;
  right: 15px;
  bottom: 35px;
  position: fixed;
  transition: height 0.2s ease,width 0.5s ease,transform 0s ease,opacity 0.6s ease;
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  justify-content: space-between;
  background-color: transparent;
  border-radius: 10px;
}
.hoverCard{
  transition: transform 0.5s ease;
  transform: translateX(-25px);
  opacity: 1;
}
.card{
  z-index: 9999;
  border-radius: 10px;
  background-color: white;
  box-shadow: 0px 0px  5px 2px rgba(0, 0, 0, 0.2);
  height: 220px;
  width: 220px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}
.noshow-card{
  height: 100px;
  width: 10px;
  opacity: 0;
}
.card >h1{
  color: rgba(0,0,0,0.65);
  font-size: 13px;
}
.chat-windows{
  transition: all 0.3s ease;
  opacity: 0;
  height: 0;
  z-index: 9999;
  position: fixed;
  right: 45px;
  bottom: 20px;
  width: 400px;
  background-color: #fff;
  border-radius: 5px;
  overflow: hidden;
  box-shadow: 0 8px 8px rgba(100, 100, 100, 0.5);
  display: flex;
  flex-direction: column;
  align-items: center;
}
.windows-header{
  background-image: url("https://tentech.oss-cn-shenzhen.aliyuncs.com/custom/20231019162909.png");
  background-size: cover;
  z-index: 3;
  top: 0;
  position: sticky;
  width: 100%;
  height: 70px;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;

}
.chat-logo-container{
  padding-left: 20px;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: flex-start;
}
.chat-logo-container>img{
  width: 50px;
  object-fit: cover;
  border-radius: 1000px;
  margin-right: 6px;
}
.chat-logo-container>p{
  color: #fff;
}
.welcome{
  padding-top: 10px;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-start;
}
.welcome>h1{
  font-size: 16px;
  color: #fff;
}
.welcome>p{
  font-weight: 100;
  font-size: 12px;
  color: #fff;
}
.show-chat-windows{
  opacity: 1;
